mapper.select:
SELECT t.id, t.name, t.request_url as "requestUrl", (CASE WHEN aup.id = 0 OR aup.id IS NULL THEN false ELSE true END) AS "permission", (CASE WHEN cat.id = 0 OR cat.id IS NULL THEN '' ELSE cat.name END) AS "typeName" FROM crm_api_interface t left join crm_api_type cat on t.type_id = cat.id LEFT JOIN crm_api_user_permission aup on t.id = aup.interface_id AND aup.api_user_id = #{apiUserId} WHERE 1 = 1 and t.status = '1'
AND t.name LIKE CONCAT('%', #{name}, '%')
AND t.type_id = #{typeId}
ORDER BY id DESC LIMIT #{limit} OFFSET #{offset}

SELECT count(0) FROM crm_api_interface t WHERE 1=1 and t.status = '1'
AND t.name LIKE CONCAT('%', #{name}, '%')
AND t.type_id = #{typeId}
